import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { storage } from "../firebase/index";
import Blog from './blog';

class ImageUpload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: null,
      url: "",
      progress: 0,
      name:"",
      description:""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleUpload = this.handleUpload.bind(this);
  }

  handleChange = e => {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      this.setState(() => ({ image }));
    }
  };
  handleName = event => {
    // console.log(event.target.value);
    this.setState({ name: event.target.value });
  };

  handleDescription = event => {
    this.setState({ description: event.target.value });
  };

  handleUpload = () => {
    const { image,name,description } = this.state;
    const uploadTask = storage.ref(`${name}+${description}`).put(image);

    console.log("Upload", image, uploadTask);
    uploadTask.on(
      "state_changed",
      snapshot => {
        // progrss function ....
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress });
      },
      error => {
        // error function ....
        console.log("ERR");
        console.log(error);
      },
      () => {
        // complete function ....
        console.log("Complete");
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            console.log(url);
            this.setState({ url });
          });
      }
    );
  };

  // writeUserData = ( image) => {
  //   storage.ref("data").set({
  //     plaseName: this.props.name,
  //     description: this.props.description,
  //     image: image
  //   });
  // };

  render() {
    const {state,handleDescription,handleName,handleChange,handleUpload}=this;
    const {name,description}=state;
    const style = {
      // height: '100vh',
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center"
    };

    return (
      <div style={style}>
        {/* <progress value={this.state.progress} max="100"/> */}
        <p class="font-weight-bold">Place name:</p>
            <input type="text" value={name} onChange={handleName} />

            <p class="font-weight-bold">Description:</p>
            <textarea
              cols="20"
              rows="5"
              value={description}
              onChange={handleDescription}
            ></textarea>
        <input type="file" onChange={handleChange} />
        <button onClick={handleUpload}>UPLOAD</button>
        <br />
        {/* <img src={this.state.url } alt="Uploaded images" height="300" width="400"/> */}
        <Blog  />
      </div>
    );
  }
}

export default ImageUpload;
