import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import ImageUpload from "./ImageUpload";
export default class Rahma extends Component {
  state = {
    name: "",
    description: ""
  };

  handleSubmit = event => {
    event.preventDefault();
    this.firebaseRefs["data"].push({
      name: this.state.name,
      description:this.state.description,

    });
    this.setState({ text: "" });
  };

  handleName = event => {
    // console.log(event.target.value);
    this.setState({ name: event.target.value });
  };

  handleDescription = event => {
    this.setState({ description: event.target.value });
  };

  render() {
    const { state, handleSubmit, handleName, handleDescription } = this;
    const { name, description } = state;
    return (
      <div>
        <form className="form-inline" onSubmit={handleSubmit}>
          <center>
            <p class="font-weight-bold">Place name:</p>
            <input type="text" value={name} onChange={handleName} />

            <p class="font-weight-bold">Description:</p>
            <textarea
              cols="20"
              rows="5"
              value={description}
              onChange={handleDescription}
            ></textarea>
            <ImageUpload data={state} />
            <button onClick="button" class="btn btn-primary">
              Submit
            </button>
          </center>
        </form>
      </div>
    );
  }
}
