import React, { Component } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Blog from "./blog";

class Share extends Component {
  state = {
    data: []
  };

  handleSubmit = event => {
    event.preventDefault();
  };

  handleName = event => {
    // console.log(event.target.value);
    this.setState({ data: event.target.value });
  };

  handleDescription = event => {
    this.setState({ data: event.target.value });
  };

  //==========================MOUNTING DATA FROM DB=============================//
  componentDidMount = () => {
    axios
      .get("http://localhost:20000/showBlog")
      .then(({ data }) => {
        this.setState({
          data
        });
        console.log(this.state.data);
      })
      .catch(error => {
        console.log(error);
      });
  };

  //==========================ADD DATA INTO DB=============================//
  addData = info => {
    console.log("ADD DATA");
    axios
      .post("http://localhost:20000/addData", info)
      .then(({ data }) => {
        // handle success
        console.log({ data });
        this.setState({
          data
        });
      })
      .catch(error => {
        // handle error
        console.log(error);
      });
  };

  //==========================ROUTING AND COMPONENTS=============================//
  render() {
    const {
      state,
      handleSubmit,
      handleName,
      handleDescription,
      addData
    } = this;
    // const { placeName, placeDescription } = state;
    return (
      <div>
        <form className="form" onSubmit={handleSubmit}>
          <center>
            <p class="font-weight-bold">Place name:</p>
            <input type="text" value={placeName} onChange={handleName} />

            <p class="font-weight-bold">Description:</p>
            <textarea
              cols="20"
              rows="5"
              value={placeDescription}
              onChange={handleDescription}
            ></textarea>
            <button class="btn btn-primary" onClick={addData}>
              Submit
            </button>
          </center>
        </form>
        {/* <Blog data={placeName,placeDescription} /> */}
      </div>
    );
  }
}

export default Share;
