import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import ImageUpload from "./ImageUpload";
export default class Rahma extends Component {
  state = {
    name: "",
    description: ""
  };

  handleSubmit = event => {
    event.preventDefault();
  };

  handleName = (event) => {
    this.setState({ name: event.target.value });
  }

  ha = (event) => {
    this.setState({ name: event.target.value });
  }

  render() {
    const {handleSubmit,handleName,handleDescription}=this;
    return (
      <div>
        <form className="form-inline" onSubmit={handleSubmit}>
          <center>
            <p class="font-weight-bold">Place name:</p>
            <input type="text" value={name} onChange={handleName}/>
            <br />

            <p class="font-weight-bold">Description:</p>
            <textarea cols="20" rows="5" value={description} onChange={handleDescription}></textarea>
            <br />

            <button onClick="button" class="btn btn-danger">
              submit
            </button>
          </center>
        </form>
        <ImageUpload />
      </div>
    );
  }
}

