import React, { Component } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Blog from "./blog";

class Share extends Component {
  state = {
    placeName: "",
    placeDescription: "",
    data:[]
  };

  handleSubmit = event => {
    event.preventDefault();
  };

  handleName = event => {
    // console.log(event.target.value);
    this.setState({ placeName: event.target.value });
  };

  handleDescription = event => {
    this.setState({ placeDescription: event.target.value });
  };

  //==========================MOUNTING DATA FROM DB=============================//
  componentDidMount = () => {
    axios
      .get("http://localhost:20000/showBlog")
      .then(({ data }) => {
        this.setState({ data });
        console.log(this.state.data);
      })
      .catch(error => {
        console.log(error);
      });
  };

  //==========================ADD DATA INTO DB=============================//
  addData = info => {
    console.log("ADD DATA");
    axios
      .post("http://localhost:20000/addData", info)
      .then(({ data }) => {
        // handle success
        console.log({ data });
        this.setState({ data });
      })
      .catch(error => {
        // handle error
        console.log(error);
      });
  };

  addNewData = () => {
    const { state, addData } = this;
    const { placeName, placeDescription } = state;
    if (
      placeName !== "" &&
      placeName !== null &&
      placeDescription !== "" &&
      placeDescription !== null
    ) {
      let newData = { placeName: placeName, placeDescription: placeDescription };
      addData(newData);
      // this.setState({ placeName: "", placeDescription: "" });
    }
  };

  //==========================ROUTING AND COMPONENTS=============================//
  render() {
    const {
      state,
      handleSubmit,
      handleName,
      handleDescription,
      addNewData
    } = this;
    const { data,placeName, placeDescription } = state;
    return (
      <div>
        <form className="form" onSubmit={handleSubmit}>
          <center>
            <p class="font-weight-bold">Place name:</p>
            <input type="text" value={placeName} onChange={handleName} />

            <p class="font-weight-bold">Description:</p>
            <textarea
              cols="20"
              rows="5"
              value={placeDescription}
              onChange={handleDescription}
            ></textarea>
            <button class="btn btn-primary" onClick={addNewData}>
              Submit
            </button>
          </center>
        </form>
        <Blog data={this} />
      </div>
    );
  }
}

export default Share;
